import React, { useEffect, useRef, useState } from 'react';

const CameraComponent: React.FC = () => {
  const videoRef = useRef<HTMLVideoElement | null>(null);     // „Ç´„É°„É©Êò†ÂÉè
  const canvasRef = useRef<HTMLCanvasElement | null>(null);   // „Ç≠„É£„Éó„ÉÅ„É£Áî®
  const [imageDataUrl, setImageDataUrl] = useState<string | null>(null); // ÁîªÂÉèË°®Á§∫Áî®

  // „Ç´„É°„É©Ëµ∑ÂãïÂá¶ÁêÜ
  useEffect(() => {
    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({
          video: { facingMode: 'environment' }, // ËÉåÈù¢„Ç´„É°„É©
          audio: false,
        });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (err) {
        console.error('„Ç´„É°„É©„ÅÆËµ∑Âãï„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', err);
      }
    };

    startCamera();
  }, []);

  // „Ç∑„É£„ÉÉ„Çø„Éº„ÇíÊäº„Åó„Åü„Å®„Åç
  const handleCapture = () => {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    if (!video || !canvas) return;

    const context = canvas.getContext('2d');
    if (!context) return;

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    context.drawImage(video, 0, 0, canvas.width, canvas.height);

    const dataUrl = canvas.toDataURL('image/png'); // ÁîªÂÉè„Éá„Éº„ÇøÂèñÂæó
    setImageDataUrl(dataUrl); // img„Çø„Ç∞„Å´ÂèçÊò†
  };

  return (
    <div>
      <video ref={videoRef} width="640" height="480" autoPlay playsInline />
      <br />
      <button onClick={handleCapture}>üì∏ „Ç∑„É£„ÉÉ„Çø„Éº</button>
      <canvas ref={canvasRef} style={{ display: 'none' }} />
      <br />
      {imageDataUrl && (
        <div>
          <img src={imageDataUrl} alt="ÊíÆÂΩ±ÁîªÂÉè" width="640" />
          <br />
          <a href={imageDataUrl} download="captured.png">ÁîªÂÉè„Çí‰øùÂ≠ò</a>
        </div>
      )}
    </div>
  );
};

export default CameraComponent;
