import React, { useEffect, useRef, useState } from 'react';

const CameraComponent: React.FC = () => {
  const videoRef = useRef<HTMLVideoElement | null>(null);
  const canvasRef = useRef<HTMLCanvasElement | null>(null);
  const [imageDataUrl, setImageDataUrl] = useState<string | null>(null); // Ë°®Á§∫Áî®ÁîªÂÉèURL
  const [loading, setLoading] = useState(false); // „É≠„Éº„Éá„Ç£„É≥„Ç∞Áä∂ÊÖã
  const [label, setLabel] = useState<string | null>(null);                   // Êé®Ë´ñ„Åï„Çå„Åü„É©„Éô„É´Âêç

  // „Ç´„É°„É©Ëµ∑Âãï
  useEffect(() => {
    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({
          video: { facingMode: 'environment' },
          audio: false,
        });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (err) {
        console.error('„Ç´„É°„É©„ÅÆËµ∑Âãï„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', err);
      }
    };

    startCamera();
  }, []);

  // ÊíÆÂΩ±Âá¶ÁêÜ
  const handleCapture = () => {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    if (!video || !canvas) return;

    const context = canvas.getContext('2d');
    if (!context) return;

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    context.drawImage(video, 0, 0, canvas.width, canvas.height);

    const dataUrl = canvas.toDataURL('image/png');
    setImageDataUrl(dataUrl);
  };

  // Flask„Éê„ÉÉ„ÇØ„Ç®„É≥„Éâ„Å∏ÈÄÅ‰ø°„ÅóYOLO„ÅßÊé®Ë´ñ
  const handleUpload = async () => {
    if (!imageDataUrl) return;

    setLoading(true);

    try {
      const blob = await (await fetch(imageDataUrl)).blob();
      const formData = new FormData();
      formData.append('image', blob, 'photo.png');

      const response = await fetch('http://localhost:5000/predict', {
        method: 'POST',
        body: formData,
      });
      // const response = await fetch('http://server:5000/predict', {
      //   method: 'POST',
      //   body: formData,
      // });

      if (!response.ok) throw new Error('„Éê„ÉÉ„ÇØ„Ç®„É≥„Éâ„Ç®„É©„Éº');

      // const resultBlob = await response.blob();
      // const resultUrl = URL.createObjectURL(resultBlob);
      // setImageDataUrl(resultUrl); // ÁµêÊûúÁîªÂÉè„ÇíË°®Á§∫

      const result = await response.json();
      const imgSrc = `data:image/jpeg;base64,${result.image}`;
